SELECT INV.MTL_SYSTEM_ITEMS_B.SEGMENT1 AS PN, APPS.MTL_CATEGORIES_VL.SEGMENT1, APPS.MTL_CATEGORIES_VL.SEGMENT2, APPS.MTL_CATEGORIES_VL.SEGMENT3, APPS.MTL_CATEGORIES_VL.SEGMENT4, APPS.MTL_CATEGORIES_VL.STRUCTURE_ID
FROM (INV.MTL_ITEM_CATEGORIES LEFT JOIN APPS.MTL_CATEGORIES_VL ON INV.MTL_ITEM_CATEGORIES.CATEGORY_ID = APPS.MTL_CATEGORIES_VL.CATEGORY_ID) INNER JOIN INV.MTL_SYSTEM_ITEMS_B ON INV.MTL_ITEM_CATEGORIES.INVENTORY_ITEM_ID = INV.MTL_SYSTEM_ITEMS_B.INVENTORY_ITEM_ID
GROUP BY INV.MTL_SYSTEM_ITEMS_B.SEGMENT1, APPS.MTL_CATEGORIES_VL.SEGMENT1, APPS.MTL_CATEGORIES_VL.SEGMENT2, APPS.MTL_CATEGORIES_VL.SEGMENT3, APPS.MTL_CATEGORIES_VL.SEGMENT4, APPS.MTL_CATEGORIES_VL.STRUCTURE_ID;
